mapper.update:
update 
${boardType.CategoryName}
set subject=#{bvo.subject}, content=#{bvo.content}
where bid=#{bvo.bid}

mapper.select:
		select
			result.bid, result.content, result.regdate,result.board
		from(
				select *, "arcturus" as board
				from arcturus_reply as a right join (
												select username,id
												from  user
												where id=#{id}
												) as u1
				on a.writer=u1.username
       			Union all

        		select *, "starcraft" as board
        		from starcraft_reply as s right join(
        										select username, id
												from user
             	                            	where id=#{id}
												) as u2
        		on s.writer=u2.username
			) as result
		where result.delYn=0
		order by result.regdate desc
		limit #{pageCria.startPage}, #{pageCria.numPerPage}

		select
			result.bid, result.subject, result.regdate, result.board
		from(
				select *, "arcturus" as board
				from arcturus as a right join (
												select username,id
												from  user
												where id=#{id}
												) as u1
				on a.writer=u1.username
       			Union all

        		select * ,"starcraft" as board
        		from starcraft as s right join(
        										select username, id
												from user
             	                            	where id=#{id}
												) as u2
        		on s.writer=u2.username
			) as result
		where result.delYn=0
		order by result.regdate desc
		limit #{pageCria.startPage}, #{pageCria.numPerPage}

select * from ( select sub.bcode,sub.delYn,sub.bid, sub.subject, sub.content, sub.writer, sub.regdate, sub.hit, replyCount, id from ${boardType.CategoryName} as sub
left join (select bid,count(*) as replyCount from ${boardType.CategoryName}_reply group by bid) as reply on sub.bid=reply.bid
left join ( select id, username from user ) as u on sub.writer=u.username
) as main where main.delYn=0
AND main.bcode = #{pageCria.bcode}
order by
main.bid desc, main.regdate desc
main.bid desc, main.hit desc
limit #{pageCria.startPage}, #{pageCria.numPerPage}

		select
			count(delYn)
		from(
				select a.delYn
				from arcturus as a right join (
												select username
												from  user
												where id=#{id}
												) as u1
				on a.writer=u1.username
       			Union all

        		select s.delYn
        		from starcraft as s right join(
        										select username
												from user
             	                            	where id=#{id}
												) as u2
        		on s.writer=u2.username
			) as result
		where result.delYn=0

		select
			count(delYn)
		from(
				select a.delYn
				from arcturus_reply as a right join (
												select username
												from  user
												where id=#{id}
												) as u1
				on a.writer=u1.username
       			Union all

        		select s.delYn
        		from starcraft_reply as s right join(
        										select username
												from user
             	                            	where id=#{id}
												) as u2
        		on s.writer=u2.username
			) as result
		where result.delYn=0

select count(islovers)
from
 	${boardType.CategoryName}_lovers
where bid=#{bid} 
	AND userid=#{userid} 
	AND islovers=1
